mapper.select:
SELECT u.id_usuario , u.no_nomb_usua , u.ap_pate_usua , u.ap_mate_usua , u.co_logi_usua , u.ti_docu_iden , u.nu_docu_iden , vl_corr_elec , u.vl_cont_inic_sesi , u.id_cliente , u.id_usua_crea , u.fe_usua_crea , u.de_term_crea , u.id_usua_modi , u.fe_usua_modi , u.de_term_modi , CASE u.st_regi WHEN 'A' THEN 'Activo' ELSE 'Inactivo' END as st_regi , u.ti_pa_usuario as tu_nsDetalleParametro , u.in_inicio_sesi_movi as sesionMovil FROM usuario u
LOWER(u.co_logi_usua) LIKE '%' || LOWER(#{login}) || '%' AND u.ti_docu_iden = #{idTipoDocumentoIdentidad} AND LOWER(u.nu_docu_iden) LIKE '%' || LOWER(#{numeroDocumentoIdentidad}) || '%' AND LOWER(u.vl_corr_elec) LIKE '%' || LOWER(#{correoElectronico}) || '%' AND LOWER(u.no_nomb_usua) LIKE '%' || LOWER(#{nombres}) || '%' AND LOWER(u.ap_pate_usua) LIKE '%' || LOWER(#{apellidoPaterno}) || '%' AND LOWER(u.ap_mate_usua) LIKE '%' || LOWER(#{apellidoMaterno}) || '%' AND u.ti_pa_usuario = #{idTipoUsuario} AND u.st_regi = #{estadoRegistro} AND u.id_pudo = #{idPudo} AND u.id_proveedor = #{idProveedor} AND u.id_hub = #{idHub}
ORDER BY u.co_logi_usua ASC

mapper.insert:

            SELECT sq_usuario.nextval FROM dual

        INSERT INTO USUARIO u( 
	         	u.id_usuario
	         , 	u.no_nomb_usua
	         , 	u.ap_pate_usua
	         , 	u.ap_mate_usua
	         ,	u.co_logi_usua
	         , 	u.ti_docu_iden
	         , 	u.nu_docu_iden
	         ,  vl_corr_elec
	         ,  u.vl_cont_inic_sesi
	         ,	u.id_cliente
	         ,	u.id_usua_crea
	         , 	fe_usua_crea
	         , 	u.de_term_crea
	         , 	u.st_regi
	         ,	u.ti_pa_usuario
	         , 	u.in_cmb_clave
	         ,	u.fe_vcto_clave
	         ,  u.id_pudo
	         ,  u.id_proveedor
	         ,  u.id_hub
         )
        VALUES(
        		#{idUsuario}
        	, 	#{nombre}
        	, 	#{apellidoPaterno}
        	, 	#{apellidoMaterno, jdbcType=VARCHAR}
         	,	#{login}
         	, 	#{idTipoDocumentoIdentidad}
         	, 	#{numeroDocumentoIdentidad}
         	,   #{correoElectronico}
         	, 	#{password}
         	, 	#{idCliente}
         	,   #{idUsuarioCreacion}
         	, 	#{fechaUsuarioCreacion}
         	, 	#{terminalCreacion}
         	, 	'A' 
         	,   #{idTipoUsua}
         	, 	#{inCmbClave, jdbcType=NUMERIC}
         	,	#{feVctoClave, jdbcType=DATE}
         	,	#{idPudo}
         	, 	#{idProveedor}
         	, 	#{idHub}
        )

mapper.update:
UPDATE usuario 
   SET st_regi='I'
 WHERE id_usuario= #{idUsuario}

      UPDATE usuario u
	      SET 
	      		u.vl_cont_inic_sesi = #{password}
	          ,	u.in_cmb_clave      = #{inCmbClave}
	          ,	u.fe_vcto_clave     = #{feVctoClave, jdbcType=DATE}
	          , u.id_usua_modi      = #{idUsuarioModificacion}
              , u.fe_usua_modi      = SYSDATE
              , u.de_term_modi      = #{terminalModificacion}
      WHERE u.id_usuario= #{idUsuario}

mapper.select:
      SELECT  u.id_usuario AS idUsuario,u.co_logi_usua AS login,
              u.no_nomb_usua AS nombre,u.ap_pate_usua AS apellidoPaterno,
              u.ap_mate_usua AS apellidoMaterno,
              u.no_nomb_usua || ' ' || u.ap_pate_usua || ' ' || u.ap_mate_usua AS deUsuario      
      FROM usuario u
      WHERE u.st_regi='A'

	          AND u.id_pais=#{idPais}


	          AND u.id_cliente=#{idCliente}


	          AND u.co_rol = #{idAsesorComercial}


	          AND u.co_rol = #{idRolComercial}

	      ORDER BY u.no_nomb_usua ASC
